import React, {useState} from 'react';
import axios from 'axios';
import {
  FormControl,
  TextField,
  makeStyles,
  FormLabel,
  Grid,
  Button,
  ButtonGroup,
  Box, Radio, RadioGroup, FormGroup,FormControlLabel
, Container,
Icon} from '@material-ui/core';
import {withRouter, useHistory} from "react-router-dom"
import NavBar from './NavBar';
import './App.css';
import { green, yellow} from '@material-ui/core/colors'
import Swal from 'sweetalert2';

import { withStyles } from '@material-ui/core/styles'

//Created a green version of the MUI button component for update records. Using withStyles. 



const GreenButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(green[500]),
    backgroundColor: green[500],
    '&:hover': {
      backgroundColor: green[700]
    },
    color: yellow
  }
}))(Button)


const Patient = () => {
  const [patientName, setPatientName] = useState();
  const [dob, setDob] = useState('');
  const [treatmentId, setTreatmentId] = useState('');
  const [patientId, setPatientId] = useState('');
  const [address, setAddress] = useState('');
  const [allergy, setAllergy] = useState('');
  const [gender, setGender] = useState('');

// fix gender at end 
function updatePatient(){
  const value = {
      name: patientName,
      dob: dob,
      treatmentId: treatmentId,
      patientId: patientId,
      address: address,
      allergy: allergy
  };
  axios.put(`http://localhost:8081/updatePatient/${patientName}`, value)
  .then( (response) => {
      // handle success
      var resData = response.data;
      let data = JSON.stringify(resData);
      Swal.fire({
        icon: 'success',
        title: 'Updated Patient!',


      })
  });
}




  function savePatient() {
    const value = {
      name: patientName,
      dob: dob,
      treatmentId: treatmentId,
      patientId: patientId,
      address: address,
      allergy: allergy,
    };

    axios.post('http://localhost:8081/patient', value).then((response) => {
      // handle success
      var resData = response.data;
      let data = JSON.stringify(resData);
      Swal.fire({
        icon: 'success',
        title: 'New Patient saved!',

      })
    });
  }


   const handleSavePatient = () => {
      savePatient();

   }

   const handleRadioButton = () => {
      setGender('Female')
      console.log("done");
   }


   const handleClearForm = () => {
      setDob('');
      setPatientId('');
      setPatientName('');
      setTreatmentId('');
      setAddress('');
      setAllergy('')
      setGender('');

 }




    return(

    <div>
       <Box 
       display="flex" 
       justifyContent="center"
        alignItems="center">
        <FormControl className="forms">

        <FormLabel style={{color:"#3f51b5"}} >
            <h2 >
             <strong>
               <u>  Create Patient </u>
             </strong>
           </h2>
           </FormLabel>


        <FormGroup className="forms">
           <TextField
                className="textfield"

                type="text"
                placeholder="Full Name"
                alignItems="center"
                value={patientName}
                onChange={(e) => setPatientName(e.target.value)}
                variant="outlined"
                label="Full Name"
                >
                </TextField>
               <br/>

                <TextField
                className="textfield"
                label="Date Of Birth"
                type="text"
                placeholder="Date of Birth"
                value={dob}
                onChange={(e) => setDob(e.target.value)}
                variant="outlined"

              />
                   <br/>

                   <TextField
                className="textfield"

                type="text"


                label="Address"
                openTo="year"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                variant="outlined"

              />
                   <br/>


                 <TextField
                className="textfield"

                type="number"
                placeholder="Treatment ID"
                label="Treatment ID"
                value={treatmentId}
                onChange={(e) => setTreatmentId(e.target.value)}
                variant="outlined"

              />

         <br/>



              <TextField
                    className="textfield"
                    label="Patient ID"
                    type="number"
                    placeholder="Patient ID"
                    value={patientId}
                    onChange={(e) => setPatientId(e.target.value)}
                    variant="outlined"


             />
              <br/>

                <TextField
                className="textfield"
                label = "Allergy"

                type="number"
                placeholder="Allergy"
                value={allergy}
                onChange={(e) => setAllergy(e.target.value)}
                variant="outlined"


              />
              </FormGroup>
              <br/>

<FormControl >





                  <RadioGroup
        row
        aria-label="gender"
        defaultValue="female"
        name="radio-buttons-group"
        className="radioButton"

        >
        <FormControlLabel value="female" control={<Radio />} label="Female" />
        <FormControlLabel value="male" control={<Radio />} label="Male" />
        <FormControlLabel value="other" control={<Radio />} label="Other" />


        </RadioGroup>

        </FormControl>
        <Grid className="patientButtons">
              <ButtonGroup variant="outlined"  className="smallButton">
              <Button
                color="primary"
                variant="contained"
                className="smallButton"
                onClick={handleSavePatient}
                style={{backgroundcolor: "red"}}


              >
                Save Patient
              </Button>


              <Button
                color="secondary"
                variant="contained"
                className="smallButton"
                onClick={handleClearForm}


              >
                Clear Form
              </Button>



              <GreenButton
             variant='contained'
              color='secondary'
              onClick={updatePatient}

              >Update Patient</GreenButton>


         </ButtonGroup>
         </Grid>


    </FormControl>



        </Box>


    </div>
    )

}

export default Patient;

//https://www.youtube.com/watch?v=-XKaSCU0ZLM&t=2783s for notes and also to help with building
