import "./App.css";
import "./App.css";
import React , {useState, useEffect} from "react"
import axios from 'axios';
import {Grid, Button, TableLabel, TableContainer, Table, Paper, TableHead, TableRow, TableCell, TableBody, makeStyles} from '@material-ui/core';

// I need to useEffect to render the table once automatically so it's not just a blank page. Add a filter by Category .filter() select  Then return the filtered data


const useStyles = makeStyles((theme) => ({
    tableHead: {
        borderBottomStyle: "solid",
        borderBottomColor: "blue"
    },
    stickyHeader:{

    }
}));



export default function TreatmentTable() {

    const classes = useStyles();
    const [treatments, setTreatments] = useState([]);
    const [patientsById, setPatientsById] = useState([]);

  const [displayTreatments, setDisplayTreatments] = useState(true);

    function fetchTreatmentRecords() {
      axios.get('http://localhost:8081/treatment').then((response) => {
        // handle success
        setTreatments(response.data);
      });
    } 


  function displayTreatmentHandler() {
    fetchTreatmentRecords();
  }

  useEffect(() => {
    fetchTreatmentRecords();
  }, []);


  function fetchPatientRecordsById(patientId1) {
    axios
      .get(`http://localhost:8081/treatmentSearch/${patientId1}`)
      .then((response) => {
        // handle success
        setPatientsById(response.data);
        let data = JSON.stringify(response.data);
      window.alert('Showing Treatment!');



      });
  }

    return (
        <div>
         <main className="text-align-center">

      {  displayTreatments == true && (



          <TableContainer component={Paper} className={classes.tableContainer}>


          <Table stickyHeader sx={{ minWidth: 650 }} aria-label="simple table" variant="outlined" >
      <TableHead >
      <TableCell
              style={{
                  borderBottom: "2px solid #3f51b5", borderTop: "2px solid #3f51b5", color:"#3f51b5"


              }}


          >
             <h3> <strong>Treatment Table (Add Icon)</strong> </h3>
          </TableCell>

        <TableRow >

          <TableCell><strong> Treatment ID </strong> </TableCell>
          <TableCell align="right"> <strong> Type </strong></TableCell>
          <TableCell align="right"><strong> Category</strong> </TableCell>
          <TableCell align="right"><strong> Start Date</strong></TableCell>
          <TableCell align="right"><strong> Prescription</strong></TableCell>
          <TableCell align="right"><strong> View Patient </strong> </TableCell>
          <TableCell align="right"><strong> Delete Treatment </strong></TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        {treatments.map((row) => (
          <TableRow
            key={row.treatmentId}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
          >
            <TableCell component="th" scope="row"> {row.treatmentId} </TableCell>
            <TableCell align="right">{row.treatmentType}</TableCell>
            <TableCell align="right">{row.treatmentCategory}</TableCell>
            <TableCell align="right">{row.startDate}</TableCell>


              {row.prescription == 0 && (
              <TableCell align="right">Prescription issued!</TableCell>
              )}
              {row.prescription == 1 && (
              <TableCell align="right" className="hasPrescription"> 
              <strong> No Prescription </strong> 
              </TableCell>
              )}
              <TableCell> 
              <Grid container justifyContent="flex-end">

              <Button
                className="smallButton"
                color="primary"
                variant="outlined"
                onClick={() => {
                  fetchPatientRecordsById(row.treatmentId);
                }}


              >



                View
              </Button>
              </Grid>

                </TableCell>
                <TableCell>
                <Grid container justifyContent="flex-end">
              <Button
                className="smallButton"
                color="secondary"
                variant="outlined"

              >

                Delete 
              </Button>
              </Grid>

              </TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>





          </TableContainer>





      )}


      </main>


        </div>
    )
}
