import "./App.css";
import React , {useState, useEffect} from "react"
import axios from 'axios';
import { TableContainer, Button, Table, Paper, TableHead, TableRow, TableCell, TableBody, makeStyles} from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    tableHead: {
        borderBottomStyle: "solid",
        borderBottomColor: "blue"
    },
    stickyHeader:{

    }
}));




export default function PatientTable() {

    const classes = useStyles();

    const [patients, setPatients] = React.useState([]);
    const [displayPatients, setDisplayPatients] = useState(true);

    // will be used to render the treatment by id for the patient tables 
    const [treatmentsById, setTreatmentsById] = useState([]);
    const [displayTreatmentForPatient, setDisplayTreatmentForPatient] = useState(false);

    function fetchPatientRecords() {
        axios.get('http://localhost:8081/patient').then((response) => {
          // handle success
          setPatients(response.data);

        });
      }

      function deletePatient(patient1) {
        axios
          .delete(`http://localhost:8081/deletePatient/${patient1}`)
          .then((response) => {
            // handle success
            var resData = response.data;
            let data = JSON.stringify(resData);
            window.alert('Response recieved from server = ' + data);
          });
      }


      function deletePatientHandler() {
        deletePatient();
      }

      function fetchTreatmentRecordsById(treatmentId1) {
        axios
          .get(`http://localhost:8081/treatmentId/${treatmentId1}`)
          .then((response) => {
            // handle success
            setTreatmentsById(response.data);
            setDisplayTreatmentForPatient(true);

          });
      }

    // useEffect with an empty array at the end. This means it will be called just once when the component is mounted. 

    useEffect(() => {
        fetchPatientRecords();
      }, []);




    return (     




        <div>
        <main className="text-align-center">

        {  displayPatients == true && (



            <TableContainer component={Paper} className={classes.tableContainer}>


            <Table stickyHeader sx={{ minWidth: 650 }} aria-label="simple table" variant="outlined" >
        <TableHead >
        <TableCell
                style={{
                    borderBottom: "2px solid #3f51b5", borderTop: "2px solid #3f51b5"


                }}


            >
               <h3> <strong>Patient Table</strong> </h3>
            </TableCell>

          <TableRow >

            <TableCell><strong> Full Name </strong> </TableCell>
            <TableCell align="right"> <strong>Patient ID </strong></TableCell>
            <TableCell align="right"><strong> DOB</strong> </TableCell>
            <TableCell align="right"><strong> Address</strong></TableCell>
            <TableCell align="right"><strong> Treatment ID</strong></TableCell>
            <TableCell align="right"><strong> Allergy </strong></TableCell>
            <TableCell align="right"><strong> View Treatment </strong> </TableCell>
            <TableCell align="right"><strong> Delete Patient</strong></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {patients.map((row) => (
            <TableRow
              key={row.patientId}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.patientId}</TableCell>
              <TableCell align="right">{row.dob}</TableCell>
              <TableCell align="right">{row.address}</TableCell>
              <TableCell align="right">{row.treatmentId}</TableCell>

                {row.allergy == 0 && (
                <TableCell align="right">PATIENT HAS NO ALLERGY!</TableCell>
                )}
                {row.allergy == 1 && (
                <TableCell align="right" className="hasAllergy"> 
                <strong> PATIENT HAS ALLERGY! </strong> 
                </TableCell>
                )}
                <TableCell> 

                <Button
                  className="smallButton"
                  color="primary"
                  variant="outlined"
                  onClick={() => {
                    fetchTreatmentRecordsById(row.treatmentId);
                  }}
                >



                  View Treatment
                </Button>


                </TableCell>


                <TableCell>


                <Button
                  className="smallButton"
                  color="secondary"
                  variant="outlined"
                  onClick={() => {
                    deletePatient(row.patientId);
                  }}

                >

                  Delete Patient
                </Button>

                </TableCell>

            </TableRow>
          ))}
        </TableBody>
      </Table>





            </TableContainer>





        )}


        </main>

        <main>





        </main>

        <main>

        {displayTreatmentForPatient == true && (

        <TableContainer component={Paper} className={classes.tableContainer}>


            <Table stickyHeader sx={{ minWidth: 650 }} aria-label="simple table" variant="outlined" >
        <TableHead >
        <TableCell
                style={{
                    borderBottom: "2px solid #3f51b5", borderTop: "2px solid #3f51b5"


                }}


            >
               <h3> <strong>Treatment Table forâ€¦ FINISH</strong> </h3>
            </TableCell>

          <TableRow >

            <TableCell><strong> Treatment ID</strong> </TableCell>
            <TableCell align="right"> <strong>Type</strong></TableCell>
            <TableCell align="right"><strong> Category</strong> </TableCell>
            <TableCell align="right"><strong> Start Date </strong></TableCell>
            <TableCell align="right"><strong> Prescription</strong></TableCell>
            <TableCell align="right"><strong> Close </strong></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>

            <TableRow
              key={treatmentsById.treatmentId}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {treatmentsById.treatmentId}
              </TableCell>
              <TableCell align="right">

               </TableCell>
              <TableCell align="right">{treatmentsById.treatmentType}</TableCell>
              <TableCell align="right">{treatmentsById.treatmentCategory}</TableCell>
              <TableCell align="right">{treatmentsById.startDate}</TableCell>

                {treatmentsById.prescription == 0 && (
                <TableCell align="right">NO PRESCIPTION!</TableCell>
                )}
                {treatmentsById.prescription == 1 && (
                <TableCell align="right" className="hasPrescription"> 
                <strong> PRESCRIPTION </strong> 
                </TableCell>
                )}
                <TableCell> 

                <Button
                  className="smallButton"
                  color="primary"
