import React, {useState} from 'react';
import axios from 'axios';
import {
  FormControl,
  TextField,
  makeStyles,
  FormLabel,
  Grid,
  Button,
  ButtonGroup,
  Box, Radio, RadioGroup, FormGroup,FormControlLabel
, Container, InputLabel, MenuItem, Select,
Icon} from '@material-ui/core';
import './App.css';
import { green, orange, yellow} from '@material-ui/core/colors'
import Swal from 'sweetalert2';

import { withStyles } from '@material-ui/core/styles'


const GreenButton = withStyles((theme) => ({
    root: {
      color: theme.palette.getContrastText(green[500]),
      backgroundColor: green[500],
      '&:hover': {
        backgroundColor: green[700]
      },
      color: yellow
    }
  }))(Button)

  const OrangeButton = withStyles((theme) => ({
    root: {
      color: theme.palette.getContrastText(orange[500]),
      backgroundColor: orange[500],
      '&:hover': {
        backgroundColor: orange[700]
      },
      color: yellow
    }
  }))(Button)


export default function Allergy() {

    const [allergyId, setAllergyId] = useState('');
    const [allergyName, setAllergyName] = useState('');
    const [risk, setRisk] = useState('');
    const [patientId, setPatientId] = useState('');
    const [notes, setNotes] = useState('');


    const handleClearForm = () => (
        setAllergyId(''),
        setAllergyName(''),
        setRisk(''),
        setPatientId(''),
        setNotes('')
    )

  function createPatient() {
    const value = {
     allergyId: allergyId,
     allergyName: allergyName,
     risk: risk,
     patientId: patientId,
     notes: notes
    };

    axios.post('http://localhost:8081/createallergy', value).then((response) => {
      // handle success
      var resData = response.data;
      Swal.fire({
        icon: 'success',
        title: 'New Allergy saved!',

      })
      handleClearForm();
    });
  }

  function updateAllergy(){
    const value = {
        allergyId: allergyId,
        allergyName: allergyName,
        risk: risk,
        patientId: patientId,
        notes: notes

    };
    axios.put(`http://localhost:8081/updatePatient/${allergyId}`, value)
    .then( (response) => {
        // handle success
        var resData = response.data;
        let data = JSON.stringify(resData);
        Swal.fire({
          icon: 'success',
          title: 'Updated Patient!',


        })
        handleClearForm();

    });
  }




    return (
        <div>
        <Box 
        display="flex" 
        justifyContent="center"
         alignItems="center">
         <FormControl  className="forms">

         <FormLabel style={{color:"#3f51b5"}} >
             <h2 >
              <strong>
                <u>  Create Allergy </u>
              </strong>
            </h2>
            </FormLabel>


         <FormGroup className="forms">
            <TextField
                 className="textfield"

                 type="text"
                 placeholder="Allergy ID"
                 alignItems="center"
                value={allergyId}
                onChange={(e) => setAllergyId(e.target.value)}
                 variant="outlined"
                 label="Allergy ID"
                 >
                 </TextField>
                <br/>

                 <TextField
                 className="textfield"
                 label="Allergy Name"
                 type="text"
                 placeholder="Allergy Name"
                 value={allergyName}
                onChange={(e) => setAllergyName(e.target.value)}

                 variant="outlined"

               />
                    <br/>

                    <TextField
                 className="textfield"

                 type="text"


                 label="Risk"
                value={risk}
                onChange={(e)=> setRisk(e.target.value)}

                 variant="outlined"

               />
                    <br/>




               <TextField
                     className="textfield"
                     label="Patient ID"
                     type="number"
                     placeholder="Patient ID"
                     value={patientId}
                onChange={(e)=> setPatientId(e.target.value)}

                     variant="outlined"


              />
<br/>
<FormGroup >
<TextField 
                 className="textfield2"

                 type="number"
                 placeholder="Notes"
                 label="Notes"
                  multiline
                rows={5}
                value={notes}

                onChange={(e)=> setNotes(e.target.value)}

                 variant="outlined"


     />
     </FormGroup>


               </FormGroup>
               <br/>


         <Grid className="patientButtons">
               <ButtonGroup variant="outlined"  className="smallButton">
               <Button
                 color="primary"
                 variant="contained"
                 className="smallButton"
                 onClick={createPatient}

                 style={{backgroundcolor: "red"}}


               >
                 Save Allergy
               </Button>


               <Button
                 color="secondary"
                 variant="contained"
                 className="smallButton"



               >
                 Clear Form
               </Button>



               <GreenButton
              variant='contained'
               color='secondary'


               >Update Allergy</GreenButton>




          </ButtonGroup>
          </Grid>


     </FormControl>



         </Box>

     </div>
    )
}
