import React, {useStyles} from 'react';
import axios from 'axios';
import {
  FormControl,
  TextField,
  makeStyles,
  FormLabel,
  Grid,
  Button,
  ButtonGroup,
  Box
} from '@material-ui/core';



const Treatment = () => {


  const [treatmentType, setTreatmentType] = React.useState('');
  const [treatmentCategory, setTreatmentCategory] = React.useState('');
  const [startDate, setStartDate] = React.useState('');
  const [prescription, setPrescription] = React.useState('');
  const [treatmentId, setTreatmentId] = React.useState('');

  const handleClearForm = () => {
      setTreatmentType('')

  } 


  function saveTreatment() {
    const value = {
      treatmentId: treatmentId,
      treatmentType: treatmentType,
      treatmentCategory: treatmentCategory,
      startDate: startDate,
      prescription: prescription,
    };

    axios.post('http://localhost:8081/treatment', value).then((response) => {
      // handle success
      var resData = response.data;
      let data = JSON.stringify(resData);
      window.alert('Response recieved from server = ' + data);
    });
  }

  function saveTreatmentHandler() {
    saveTreatment();
  }






    return(

    <div>
       <Box display="flex" justifyContent="center" alignItems="center">
        <FormControl className="forms">
        <FormLabel style={{color:"#3f51b5"}} >
            <h2 >
             <strong>
               <u>  Create Treatment </u>
             </strong>
           </h2>
           </FormLabel>
           <TextField
                className="textfield"
                variant="outlined"
                type="number"
                placeholder="Treatment ID"
                className="forms"
                label="Treatment ID"
                value={treatmentId}
                onChange={(e) => setTreatmentId(e.target.value)}
              />
          <br/>
              <TextField
                className="textfield"
                variant="outlined"
                type="text"
               label="Type"
                value={treatmentType}
                onChange={(e) => setTreatmentType(e.target.value)}
                className="forms"
              />
              <br />
              <TextField
                className="textfield"
                required = {true}
                type="text"
                label="Category"
                value={treatmentCategory}
                onChange={(e) => setTreatmentCategory(e.target.value)}
                variant="outlined"
                className="forms"

              />
             <br/>



              <TextField
                className="textField"

                type="text"
                type="date"
                openTo="year"
                helperText="Treatment Start Date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                variant="outlined"

                className="forms"
              />




              <br />
              <TextField
                className="textfield"
                label="Prescription"
                type="text"
                value={prescription}
                onChange={(e) => setPrescription(e.target.value)}

                variant="outlined"
                className="forms"
              />
              <br />
              <Grid className="patientButtons"> 
              <ButtonGroup variant="outlined">
                <Button
                  color="primary"
                  variant="contained"
                  onClick={saveTreatmentHandler}


                >
                  Save Treatment
                </Button>
                <br />
                <Button
                  color="secondary"
                  variant="contained"

                >
                  Clear Form
                </Button>
              </ButtonGroup>
              </Grid>

        </FormControl>

        </Box>


    </div>
    )

}

export default Treatment;
